import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom'

import ProductForm from '../components/ProductForm';

const CreateAndDashboard = () => {
    const nav = useNavigate();

    const [productList, setProductList] = useState([])

    const deleteProduct = (productToDelete) => {
        axios.delete(`http://localhost:8000/api/products/${productToDelete._id}`)
        .then((res) => {
            console.log(res)
            setProductList(productList.filter((product) => product._id !== productToDelete._id))
        })
        .catch((err) => console.log(err))
    }

    const createProduct = (newProduct) => {
        axios
            .post('http://localhost:8000/api/products', newProduct)
            .then((results) => {
                console.log(results)
                nav('/products');
            })
            .catch((err) => console.log(err))
    };

    useEffect(() => {
        axios.get('http://localhost:8000/api/products')
            .then((res) => {
                setProductList(res.data.products)
                console.log(res)
            })
            .catch((err) => console.log(err));
    }, [])

    return (
        <div>
            <div className=''>
                <h2>Product Manager</h2>
                <ProductForm useForm={createProduct} product ={{ title: '', price: 0, description: '' }}/>
            </div>
            <h1>All Products:</h1>
            <table className='table'>
                <tbody>
                    {productList.map((product, indx) => (
                        <tr key={indx}>
                            <td>Name: <Link to={`/products/${product._id}`}><span>{product.title}</span></Link></td>
                            <td>
                            <Link to={`/products/${product._id}/edit`}>Edit</Link> |
                            {/* <button className='btn btn-danger' onClick={(() => deleteProduct(product))}>Delete</button> */}
                            </td>
                        </tr>
                    ))};
                </tbody>
            </table>
        </div>
    )
}

export default CreateAndDashboard;
