import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom'

const CreateAndDashboard = () => {
    const nav = useNavigate();

    const [productList, setProductList] = useState([])
    const [title, setTitle] = useState('')
    const [price, setPrice] = useState(0)
    const [description, setDescription] = useState('')

    const deleteProduct = (productToDelete) => {
        axios.delete(`http://localhost:8000/api/products/${productToDelete._id}`)
        .then((res) => {
            console.log(res)
            setProductList(productList.filter(product))
        })
        .catch((err) => console.log(err))
    }

    const handleSubmit = (e) => {
        e.preventDefault();

        axios
            .post('http://localhost:8000/api/products', {
                title,
                price,
                description
            })
            .then((results) => {
                console.log(results)
                nav('/products');
            })
            .catch((err) => console.log(err))
    };

    useEffect(() => {
        axios.get('http://localhost:8000/api/products')
            .then((res) => {
                setProductList(res.data.products)
                console.log(res)
            })
            .catch((err) => console.log(err));
    }, [])

    return (
        <div>
            <div>
                <h2>Product Manager</h2>
                <form onSubmit={handleSubmit}>
                    <p>
                        Title:
                        <input
                            type="text"
                            value={title}
                            onChange={(e) => setTitle(e.target.value)}
                        />
                    </p>
                    <p>
                        Price:
                        <input
                            type="integer"
                            rows='30'
                            cols='30'
                            value={price}
                            onChange={(e) => setPrice(e.target.value)}
                        />
                    </p>
                    <p>
                        Description:
                        <input
                            type="text"
                            rows='30'
                            cols='30'
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                        />
                    </p>
                    <button className='btn btn-primary'>Submit</button>
                </form>
            </div>
            <h1>All Products:</h1>
            <table className='table'>
                <tbody>
                    {productList.map((product, indx) => (
                        <tr key={indx}>
                            <td>Name: <Link to={`/products/${product._id}`}><span>{product.title}</span></Link></td>
                            <td>
                            <Link to={`/products/${product._id}/edit`}>Edit</Link> |
                            <button className='btn btn-danger' onClick={(() => deleteProduct(product))}>Delete</button>
                            </td>
                        </tr>
                    ))};
                </tbody>
            </table>
        </div>
    )
}

export default CreateAndDashboard;
